import React from "react";
import { Link, useNavigate } from "react-router-dom";

export default function Header() {
  const navigate = useNavigate();
  const isAuthed = Boolean(localStorage.getItem("token"));

  const logout = () => {
    localStorage.removeItem("token");
    navigate("/login");
  };

  return (
    <header
      style={{
        padding: "10px",
        background: "#2563eb",
        color: "#fff",
        display: "flex",
        justifyContent: "space-between",
      }}
    >
      <h2 style={{ margin: 0 }}>Astrape üõç</h2>
      <div>
        <Link style={{ margin: "0 10px", color: "#fff", textDecoration: "none" }} to="/items">
          Items
        </Link>
        <Link style={{ margin: "0 10px", color: "#fff", textDecoration: "none" }} to="/cart">
          Cart
        </Link>
        {isAuthed ? (
          <button
            onClick={logout}
            style={{ marginLeft: "10px", padding: "6px 12px", borderRadius: 6, border: "none", cursor: "pointer" }}
          >
            Logout
          </button>
        ) : (
          <>
            <Link style={{ margin: "0 10px", color: "#fff", textDecoration: "none" }} to="/login">Login</Link>
            <Link style={{ margin: "0 10px", color: "#fff", textDecoration: "none" }} to="/signup">Sign Up</Link>
          </>
        )}
      </div>
    </header>
  );
}
