import React from "react";

export default function CartItem({ item, removeFromCart }) {
  return (
    <div
      style={{
        border: "1px solid #ddd",
        padding: "10px",
        borderRadius: "8px",
        marginBottom: "10px",
      }}
    >
      <h4>{item.title}</h4>
      <p>
        ${item.price} x {item.qty}
      </p>
      <button onClick={() => removeFromCart(item._id)}>Remove</button>
    </div>
  );
}
