import React, { useEffect, useState } from "react";
import API from "../api/api";
import ItemCard from "../components/ItemCard";

export default function Items() {
  const [items, setItems] = useState([]);
  const [filteredItems, setFilteredItems] = useState([]);
  const [category, setCategory] = useState("");
  const [priceRange, setPriceRange] = useState([0, 1000]);
  const [categoriesInput, setCategoriesInput] = useState("");

  const fetchItems = async () => {
    const params = {};
    if (category) params.category = category;
    if (priceRange[1] != null) params.maxPrice = priceRange[1];
    const res = await API.get("/items", { params });
    setItems(res.data);
    setFilteredItems(res.data);
  };

  const addToCart = async (item) => {
    const token = localStorage.getItem("token");
    if (token) {
      await API.post("/cart", { itemId: item._id, qty: 1 });
      alert("Added to cart!");
    } else {
      const guestCart = JSON.parse(localStorage.getItem("guest_cart") || "[]");
      const idx = guestCart.findIndex((e) => e.itemId === item._id);
      if (idx >= 0) guestCart[idx].qty += 1;
      else guestCart.push({ itemId: item._id, qty: 1, price: item.price, title: item.title });
      localStorage.setItem("guest_cart", JSON.stringify(guestCart));
      alert("Added to cart (guest)");
    }
  };

  const handleFilter = () => {
    let result = items;
    if (category) {
      result = result.filter((i) => i.category === category);
    }
    result = result.filter(
      (i) => i.price >= priceRange[0] && i.price <= priceRange[1]
    );
    setFilteredItems(result);
  };

  useEffect(() => {
    fetchItems();
    // eslint-disable-next-line react-hooks/exhaustive-deps
  }, [category, priceRange[1]]);

  useEffect(() => {
    handleFilter();
  }, [category, priceRange]);

  return (
    <div style={{ padding: "20px" }}>
      <h2>Items</h2>
      <div style={{ marginBottom: "20px" }}>
        <div style={{ display: "flex", gap: 16, alignItems: "center", flexWrap: "wrap" }}>
          <label>
            Category:
            <input
              type="text"
              value={category}
              onChange={(e) => setCategory(e.target.value)}
              placeholder="e.g. Electronics"
            />
          </label>
          <label>
            Categories (comma separated):
            <input
              type="text"
              value={categoriesInput}
              onChange={(e) => setCategoriesInput(e.target.value)}
              placeholder="Electronics,Books"
            />
          </label>
        <label style={{ marginLeft: "20px" }}>
          Price range:
          <input
            type="number"
            value={priceRange[0]}
            onChange={(e) =>
              setPriceRange([Number(e.target.value), priceRange[1]])
            }
            style={{ width: "60px" }}
          />
          -
          <input
            type="number"
            value={priceRange[1]}
            onChange={(e) =>
              setPriceRange([priceRange[0], Number(e.target.value)])
            }
            style={{ width: "60px" }}
          />
        </label>
        </div>
      </div>
      <div
        style={{
          display: "grid",
          gridTemplateColumns: "repeat(auto-fill, minmax(200px, 1fr))",
          gap: "16px",
        }}
      >
        {filteredItems.map((item) => (
          <ItemCard key={item._id} item={item} addToCart={addToCart} />
        ))}
      </div>
    </div>
  );
}
